class Solution {
    // 暴力法（不可取）
    fun maxScoreSightseeingPair2(values: IntArray): Int {
        var max = 0
        for(i in values.indices){
            for(j in i+1 .. values.lastIndex){
                max = maxOf(max, values[i] + values[j] + i -j)
            }
        }
        return max
    }

    // 脑力法
    fun maxScoreSightseeingPair(values: IntArray): Int {
        var maxi = maxOf(values[0], values[1] + 1)
        var max = values[0] + values[1] - 1
        for(i in 2..values.lastIndex){
            max = maxOf(max, values[i] - i + maxi)
            maxi = maxOf(maxi, values[i] + i)
        }
        return max
    }
}

